INSERT INTO tg_messages_curriculumsbuttons VALUES(1,'Спортклуб',replace(replace('Привет, друзья! 🌟 \r\n\r\n\r\nДобро пожаловать в наш спортивный клуб РНИМУ! Здесь вы найдете множество захватывающих секций, где можно заниматься спортом, совершенствовать свои навыки и заводить новых друзей! \r\n\r\nПрисоединяйтесь к нам, если хотите провести время с пользой, улучшить физическую форму и стать частью дружной команды! 🌟🔥 \r\n\r\nhttps://vk.com/sportclub_rnimu\r\n\r\nhttps://rsmu.ru/facilities/sport/sport-club/','\r',char(13)),'\n',char(10)),'curriculumsbuttons_img/TAuc-1YqsGY.jpg','A',1);
