INSERT INTO tg_messages_curriculumsbuttons VALUES(3,'Землячество',replace(replace('ССЗ - это направление внеучебной деятельности, которое объединяет студентов разных стран и народностей. Это большая семья, в которой тепло и уютно, вкусно и атмосферно, кажется, что и не уезжал из дома.\r\n\r\nНа сегодняшний день в состав ССЗ входят 23 землячества, и их число растет с каждым годом!\r\n\r\n🤝Мы помогаем студентам освоиться в новой для них культурной и студенческой среде, создаем комфортные условия для реализации и развития их идей.\r\n\r\nПредседатель ССЗ: https://vk.com/en.salya\r\n\r\nСсылка на сообщество VK: https://vk.com/zemlyachestva_rnimu','\r',char(13)),'\n',char(10)),'curriculumsbuttons_img/pHbVh4dwqNU.jpg','A',1);
INSERT INTO tg_messages_curriculumsbuttons VALUES(4,'Тьюторство',replace(replace('Тьютор — это наставник, который помогает им в освоении новых предметов и адаптации в нашем университете, делает студенческую жизнь понятнее, легче и интереснее.\r\n\r\nНаше Движение включает 3 направления тьюторов:\r\n● тьютор академической группы\r\n● тьютор-преподаватель\r\n● тьютор общежития','\r',char(13)),'\n',char(10)),'curriculumsbuttons_img/O_NblryCnqo_PXQpxm7.jpg','A',1);
INSERT INTO tg_messages_curriculumsbuttons VALUES(5,'Донорство',replace(replace('На протяжении многих лет РНИМУ им. Н.И. Пирогова является \r\nпризнанным лидером в области студенческого донорского движения.\r\nДонорство – это исключительная возможность подарить свою кровь \r\nнезнакомому человеку, который в ней нуждается и спасти его жизнь. \r\nИскусственной альтернативы продуктам крови на данный момент не \r\nсуществует. \r\nПожертвованная донорами кровь используется при сложных операциях \r\nили родах, кровотечениях, тяжелых травмах, ожогах и других заболеваниях. \r\nКровь одного донора разделяется на компоненты и благодаря этому может \r\nспасти жизнь 4-5 пациентам.\r\nВ нашем университете можно как стать донором крови и её \r\nкомпонентов, так и вступить в регистр доноров костного мозга.','\r',char(13)),'\n',char(10)),'curriculumsbuttons_img/FXz1OaVa9mg.jpg','A',1);
INSERT INTO tg_messages_curriculumsbuttons VALUES(6,'Волонтёрство',replace(replace('Мы хотим познакомить вас с Волонтерским центром нашего университета🌟\r\n\r\nВолонтерский центр - это:\r\n➢ 8 направлений волонтерской деятельности\r\n➢ более 800 волонтеров\r\n➢ более 10 школ-партнеров\r\n➢ 6 фондов-партнеров\r\n➢ помощь детским больницам\r\n➢ помощь взрослым больницам\r\n➢ помощь школам-интернатам/детским домам\r\n\r\n\r\nМы занимаемся социальным, событийным, спортивным, культурным, медицинским и экологическим,образовательным и патриотическим волонтёрством. \r\nУ нас каждый сможет найти для себя что-то интересное!\r\n\r\n\r\nЗа более подробной информацией обращайтесь в группу Волонтерского центра РНИМУ имени Н.И. Пирогова 📝\r\n\r\nhttps://vk.com/volrnimu 📍\r\n\r\nМы ждем тебя в состав нашей доброй команды!','\r',char(13)),'\n',char(10)),'curriculumsbuttons_img/изображение_2024-08-06_125718537.png','A',1);
