INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(2,'Counter-Strike 2.',replace(replace('Одна из самых популярных секций в CYBER RNM.\r\nИмеется несколько составов разного уровня игры.\r\n\r\nRNIMU-1 8-10 уровень FAICET,подходит для ребят желающих попасть в киберспорт - капитан https://vk.com/nukleot1d\r\n\r\nRNIMU-2 Состав состоит из игроков имеющих 13,000-18,000 рейтинга в CS 2 и от калаша с венками в СS:GO. Подойдёт игрокам выше любителей - капитан https://vk.com/drslyowl\r\n\r\nRNIMU-3 Ребята играющие в своё удовольствие, ведут свои соц.сети и выкладывают хайлайты. подойдёт для игроков играющих на рейтинге до 12.000 и до калаша в CS:GO\r\nКапитан https://vk.com/mqnwinner\r\n\r\nRNIMU-4 Состав новичков , ребята отвечают за посты в соц.сетях, отметки на тренировках и за организацию внутривузовских мероприятий. Капитан https://vk.com/medic2004','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/i_5.webp','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(3,'League of legends.',replace(replace('Одна из самых популярных моба игр на мировой арене.\r\nИмеется несколько составов:\r\n\r\nCRNM-1\r\nРейтинг: золото - грандмастер. Подойдёт для игроков, которые хотят добиться успеха в данной дисциплине и имеющих желание побеждать на турнирах.\r\nКапитан команды: https://vk.com/within_reflection\r\n\r\nCRNM-2\r\nКоманда новичков.\r\nПодойдëт для людей, которые играют в своё удовольствие.\r\nКапитан команды: https://vk.com/helldy_34','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/rsmu_09sd5yu.jpg','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(4,'Valorant.',replace(replace('Захватывающий тактический шутер, требующий от игроков меткую стрельбу, грамотное применение суперспособностей и слаженную командную игру для победы. Ведется активный набор агентов в Протокол VALORANT. Будущее за радиантами!\r\nКапитан https://vk.com/drslyowl','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/i_3.webp','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(9,'Apex Legends.',replace(replace('Ведущий шутер в классическом понятии battle royale, не изменяющий своим истокам.\r\nЖдём в нашем коллективе новых легенд.\r\nГлавный симулякр - https://vk.com/alwppp','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/i_4.webp','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(10,'Rainbow Six Siege.',replace(replace('Одна из самых молодых секций в киберспорте. С тактической точки зрения самый интересный 3D-шутер в мире. Капитан ведёт активный набор в секцию.\r\nКапитан - https://vk.com/lightweightbaabyy','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/Халява-Rainbow-Six-Siege-ключик-nvidia-2356622.jpeg','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(11,'Hearthstone.',replace(replace('Популярная карточная игра по мотивам серии Warcraft.\r\nCуществует два популярных режима Battlegrounds и Ladder. Среди сообщества врачей турниры по hearthstone имею особую ценность.','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/heartstone.jpg','https://vk.com/cyberrnimu','A',1);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(12,'Гранулоциты',replace(replace('Гранулоциты— это вид лейкоцитов, в которых нуждаются больные \r\nонкологическими заболеваниями крови и костного мозга. Донорские \r\nгранулоциты могут храниться всего несколько часов, поэтому их берут \r\nкрайне редко— только для конкретного пациента. \r\nЕсли вы заинтересовались донорством гранулоцитов или давно хотели \r\nадресно помочь маленьким пациентам РДКБ и НМИЦ ДГОИ им Д.Рогачева, \r\nоставьте свои данные куратору направления, а мы будем сообщать о \r\nнеобходимости сдачи гранулоцитов при нахождении на лечении пациентов, \r\nнуждающихся в переливании гранулоцитов.\r\nКуратор: https://vk.com/iiamfirefox','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/granulocity_norma.jpg','https://donor.rsmu.ru','A',34);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(13,'Тромбоциты и плазма:',replace(replace('Если у тебя келл-положительная кровь, и тебе отказывают в сдаче цельной \r\nкрови, но ты мечтаешь быть донором, то можно отправиться на сдачу \r\nкомпонентов крови — тромбоцитов и плазмы.\r\nОни очень нужны в РДКБ и НМИЦ ДГОИ им. Д.Рогачева при лечении детей \r\nс тяжелыми заболеваниями.\r\nТромбоциты.  Главная функция тромбоцитов – участие в процессе \r\nсвертывания крови – важной защитной реакции организма, \r\nпредотвращающей большую кровопотерю при ранении сосудов. Показанием \r\nк переливанию донорских тромбоцитов является заболевания системы крови, \r\nт.ч. лейкоз, снижение тромбоцитов у пациента, которое обычно связано с \r\nпроведением химиотерапии, а также при обильных кровотечениях (травмы, \r\nроды, проведение хирургических операций).\r\nПлазма. За раз донор может сдать до 600 мл плазмы.  При этой процедуре у \r\nдонора забирается жидкая часть крови, на 90% состоящая из воды — плазма. \r\nПлазма нужна для переливания людям после ожогов и травм, а также она \r\nиспользуется в качестве компонента для создания препаратов крови.','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/trombocit.jpg','https://donor.rsmu.ru','A',34);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(16,'Как попасть к нам в команду?',replace(replace('Каждый год мы рады видеть новых, заинтересованных и ярких людей в нашей семье под названием «Эйфория». \r\nНабор осуществляется в начале каждого семестра, по предварительной записи. За более подробной информацией можете перейти в нашу группу ВК, телеграмм-канал или обращаться к нашему тренеру или капитану команды.','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/EfNgD9V5lQU.jpg','https://vk.com/cheer_rnimu','A',49);
INSERT INTO tg_messages_subsubcurriculumsbuttons VALUES(17,'Наши достижения',replace(replace('Наша секция Чирлидинга была основана в 2019 году, и к настоящему времени уже завоевала множество наград и приняла участите в большом количестве масштабных соревнований, таких как: \r\n1. Национальные соревнования (всероссийские) \r\n2. RCO - Russian Cheer Open (межрегиональные) \r\n3. Кубок Москвы \r\n4. Чемпионат и Первенство Москвы \r\n5. МССИ - московские студенческие спортивные игры \r\nТакже в последние 2 года команда выезжает в другие города на соревнования, откуда привозит только первые места!) \r\n\r\nПомимо соревновательной деятельности наша сборная выступает в качестве группы поддержки на матчах ВУЗа, а также на играх различных городских команд. \r\nВ команде всегда царит рабочая, но веселая атмосфера, коллектив очень дружный и открытый)','\r',char(13)),'\n',char(10)),'subsubcurriculumsbuttons_img/09jxWflT1P4.jpg','https://vk.com/cheer_rnimu','A',49);
