INSERT INTO online_elder_eldermessage VALUES(1,'Главное сообщение',replace(replace('Знакомьтесь - ОнСТА!\r\nОнСТАроста - Он Самый Технологичный Ассистент\r\n\r\nОнСТА - “Онлайн-староста” -  виртуальный староста университета и ваш главный помощник во всех организационных вопросах учебы.\r\n\r\nОнСТА владеет всей важной информацией, знает последние новости и ответит на все ваши вопросы, касающиеся обучения и дополнительного образования.\r\n\r\nУниверсальный помощник  - ОнСТА - для студентов и старост на пути к эффективной коммуникации с кафедрами, деканатом и ректоратом!\r\n\r\nДавайте учиться и становиться профессионалами с информационным комфортом!\r\nОнСта - Ваш ассистент Роста))','\r',char(13)),'\n',char(10)),'eldermessage_img/photo_2024-07-30_08-11-01.jpg','A');
INSERT INTO online_elder_eldermessagebuttons VALUES(1,'Стипендия',replace(replace('Стипендия — это материальная поддержка студентов, которые обучаются в высших учебных заведениях, колледжах и техникумах.\r\n\r\nГосударственную академическую стипендию студентам 2-6 курсов, обучающимся за счет средств \r\nфедерального бюджета, в размере:\r\n\r\n1.студентам, обучающимся на «хорошо» - 2 200,00 руб. \r\n\r\n2.студентам, обучающимся на «хорошо и отлично» 2 200,00 руб. \r\n\r\n3.студентам, обучающимся на «отлично» - 4 350,00 руб. \r\n\r\nТак же предусмотрена повышенная стипендия за различные достижение,о чем мы расскажем далее.\r\n\r\nПодробнее о положении вы можете ознакомиться по ссылке.','\r',char(13)),'\n',char(10)),'eldermessagebuttons_img/rnimu2.jpg','https://rsmu.ru/sveden/grants/','A',1);
